mapper.select:
select *
  from tb_user
 where user_id = #{id}
   and user_pw = #{pw}

select count(1)
  from tb_user
 where user_id = #{id}

select count(1)
  from tb_user
 where user_id = #{id}
   and user_pw = #{pw}

mapper.update:
UPDATE TB_USER
   SET NICKNAME = #{nickname}
     , USER_PW = #{userPw}
     , EMAIL = #{email}
     , PHONE = #{phone}
     , ADDRESS = #{address}
     , ADDRESSDETAIL = #{addressdetail}
 WHERE USER_ID = #{userId}
