mapper.select:
select *
  from tb_user
 where user_id = #{id}
   and user_pw = #{pw}

select count(1)
  from tb_user
 where user_id = #{id}

select count(1)
  from tb_user
 where user_id = #{id}
   and user_pw = #{pw}

mapper.update:
UPDATE TB_USER
   SET NICKNAME = #{nickname}
     , USER_PW = #{userPw}
     , EMAIL = #{email}
     , PHONE = #{phone}
     , ADDRESS = #{address}
     , ADDRESSDETAIL = #{addressdetail}
 WHERE USER_ID = #{userId}

mapper.insert:
INSERT INTO TB_SIGNKEY
	(USER_ID, SIGNKEY)
SELECT USER_ID, #{signkey}
FROM TB_USER
WHERE USER_NAME = #{user.userName}
  AND BIRTH = #{user.birth}
  AND IDENTI = #{user.identi}
  AND lower(email) = lower(#{user.email})

mapper.select:
  SELECT USER_ID, USER_PW
	FROM TB_USER
	WHERE USER_NAME = #{userName}
	  AND BIRTH = #{birth}
	  AND IDENTI = #{identi}
	  AND lower(email) = lower(#{email})
